/* MODULE xPos. */

#include "xPos.h"
#define xPos_MIN_PWM_US          900   /*!< minimal duty cycle for servo */
#define xPos_MAX_PWM_US          2100  /*!< maximal duty cycle for servo */
#define xPos_INIT_PWM_US         1500  /*!< initial PWM duty */
#define xPos_PWM_PERIOD_US       20000 /*!< PWM period in micro seconds, typically 20'000 us (50 Hz) */
#define xPos_PWM_RANGE_US        (xPos_MAX_PWM_US-xPos_MIN_PWM_US) /*!< PWM range in micro seconds */
#define xPos_POS8_TO_PWM(pos)    (uint16_t)(xPos_MIN_PWM_US+(((pos)*(uint32_t)xPos_PWM_RANGE_US)/255)) /*!< Position to PWM transformation */
#define xPos_PWM_TO_POS8(pwm)    (uint16_t)((((uint32_t)(pwm)-xPos_MIN_PWM_US)*255/xPos_PWM_RANGE_US)) /*!< PWM to position transformation */
#define xPos_SHELL_NAME_STR      "xPos" /*!< Name used for servo in shell */

/* inverted PWM: if set to 1, then a duty value of 0xffff means signal is all time low, where 0x0000 means signal is high all time */
#define xPos_INVERTED_PWM   0

typedef struct xPos_ServoDescTag {
  uint16_t currPWMus; /* current duty in microseconds */
} xPos_ServoDesc;

/*! \brief Descriptor about the status of the servo */
static xPos_ServoDesc Servo;

/* Internal method prototypes */
static void DoSteps(void);

/*
** ===================================================================
**     Method      :  xPos_SetPWMDutyUs (component Servo)
**     Description :
**         Sets the servo PWM duty cycle in micro seconds (us)
**     Parameters  :
**         NAME            - DESCRIPTION
**         dutyUs          - Duty cycle in microseconds
**     Returns     : Nothing
** ===================================================================
*/
void xPos_SetPWMDutyUs(word dutyUs)
{
  /* write new duty cycle */
#if xPos_INVERTED_PWM
  Pwm2_SetDutyUS(xPos_PWM_PERIOD_US-dutyUs); /* need to invert duty cycle */
#else
  Pwm2_SetDutyUS(dutyUs);
#endif
  Servo.currPWMus = (uint16_t)dutyUs; /* update current duty cycle value */
}

/*
** ===================================================================
**     Method      :  xPos_SetPos (component Servo)
**     Description :
**         Sets the servo position
**     Parameters  :
**         NAME            - DESCRIPTION
**         pos             - Position to be used
**     Returns     : Nothing
** ===================================================================
*/
void xPos_SetPos(byte pos)
{
  xPos_SetPWMDutyUs((uint16_t)xPos_POS8_TO_PWM(pos));
}

/*
** ===================================================================
**     Method      :  xPos_Init (component Servo)
**     Description :
**         Moves the serve to its initialization position.
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void xPos_Init(void)
{
  xPos_SetPWMDutyUs(xPos_INIT_PWM_US); /* set servo to initial duty ratio */
}

/*
** ===================================================================
**     Method      :  xPos_PWMusToPos8 (component Servo)
**     Description :
**         Transforms a micro-second PWM time into 8bit position
**     Parameters  :
**         NAME            - DESCRIPTION
**         us              - us PWM time
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
byte xPos_PWMusToPos8(word us)
{
  uint16_t pos, time;

  pos = xPos_PWM_TO_POS8(us);
  time = xPos_POS8_TO_PWM(pos);
  if (time<us) {
    return (byte)(pos+1); /* add rounding */
  } else {
    return (byte)pos;
  }
}

